 private Button btnSaveSleepTime;
 private TextView txtSleepHistory;

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 setContentView(R.layout.activity_sleep_tracking);

 initializeViews();
 setupToolbar();
 setupClickListeners();
 }

